
def add(x, y):
    return x + y

def subtract(x, y):
    return x - y

def multiply(x, y):
    return x * y

def divide(x, y):
    if y == 0:
        return "Error! Division by zero."
    else:
        return x / y

print(add(2,2))
print(subtract(2,2))
print(multiply(2,2))
print(divide(2,2))